mapper.select:
/*ChannelGroupMapper.selectChannelGroup*/
SELECT

FROM
FI_CBPAY_CHANNEL_GROUP
WHERE
GROUP_ID=#{groupId,jdbcType=BIGINT}
AND
CCY=#{ccy,jdbcType=VARCHAR}

/*ChannelGroupMapper.selectGroupList*/
SELECT
DISTINCT GROUP_ID
FROM
  FI_CBPAY_CHANNEL_GROUP
WHERE
  CCY = #{ccy,jdbcType=VARCHAR}
AND
  CHANNEL_TYPE = #{channelType,jdbcType=TINYINT}
