mapper.select:
/**TAreaInfoMapper.queryProvince**/
SELECT
AREA_CODE as cityId,
AREA_NAME as cityName
FROM T_AREA_INFO
WHERE PARENT_AREA_CODE = #{cityId,jdbcType=VARCHAR}

/**TAreaInfoMapper.queryProvince**/
SELECT
AREA_CODE as areaId,
AREA_NAME as areaName
FROM T_AREA_INFO
WHERE PARENT_AREA_CODE = #{areaId,jdbcType=VARCHAR}
