mapper.select:
/* TUserStoreMapper.selectUserStore*/
SELECT

FROM
T_USER_STORE
WHERE
USER_NO = #{userNo,jdbcType=BIGINT}

    AND STORE_STATE =#{storeState}

/* TUserStoreMapper.selectByStoreNo*/
SELECT

FROM
T_USER_STORE
WHERE
STORE_NO= #{storeNo,jdbcType=BIGINT}

/* TUserStoreMapper.selectAll*/
SELECT

FROM
  T_USER_STORE
WHERE
  USER_NO = #{userNo,jdbcType=BIGINT}

    AND STORE_NAME =#{storeName,jdbcType=VARCHAR}


    AND TRADE_CCY =#{tradeCcy,jdbcType=VARCHAR}

/* TUserStoreMapper.selectBySellerId*/
SELECT

FROM
T_USER_STORE
WHERE
SELLER_ID = #{sellerId,jdbcType=VARCHAR}
