mapper.update:
/**TUserWithdrawSumMapper.updateWithdrawSum**/ UPDATE T_USER_WITHDRAW_SUM
TRANSFER_STATE = #{transferState,jdbcType=INTEGER},
REMARKS = #{remarks,jdbcType=VARCHAR},
UPDATE_AT = NOW()
WHERE WITHDRAW_BATCH_ID = #{withdrawBatchId,jdbcType=BIGINT}

mapper.select:
/**TUserWithdrawSumMapper.selectUserWithdrawSumPageInfo**/
SELECT

FROM
T_USER_WITHDRAW_SUM



        AND CREATE_AT >= #{beginTime,jdbcType=VARCHAR}




        AND CREATE_AT <= #{endTime,jdbcType=VARCHAR}



        AND WITHDRAW_BATCH_ID = #{withdrawBatchId,jdbcType=BIGINT}


        AND CHANNEL_NAME = #{channelName,jdbcType=VARCHAR}


        AND WITHDRAW_CCY = #{withdrawCcy,jdbcType=VARCHAR}


        AND TRANSFER_STATE = #{transferState,jdbcType=INTEGER}


ORDER BY CREATE_AT DESC
